import React from "react";
import { TouchableOpacity, View } from "react-native";
import tw from "twrnc";
import { Icon } from "react-native-elements/dist/icons/Icon";
import NavigateCard from "../../../components/RideandMap/NavigateCard";
import { createStackNavigator } from "@react-navigation/stack";
import RideOptionsCard from "../../../components/RideandMap/RideOptionsCard";
import Map from "../../../components/RideandMap/Map";
import { useNavigation } from "@react-navigation/native";
import { useDispatch } from "react-redux";
import { AuthContext } from '../../../components/context';

const MapScreen = () => {
  const { user } = React.useContext(AuthContext);
  const Stack = createStackNavigator();
  const navigation = useNavigation();
  const dispatch = useDispatch();

  return (

    <View>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("HomeScreen");
          // dispatch(setOrigin(null))
        }}
        style={[
          tw`bg-gray-100 absolute top-16 left-8 z-50 p-3 rounded-full shadow-lg`,
        ]}
      >
        <Icon name="arrow-left" />
      </TouchableOpacity>

      {/* if Driver logged in */}

      {user.role == 'Admin' && (
        <View style={tw`h-full`}>
          <Map />
        </View>
      )}

      {/* if User logged in */}

      {user.role == 'User' && (
        <View>
          <View style={tw`h-1/2`}>
            <Map />
          </View>

          <View style={tw`h-1/2 mt--5`}>
            <Stack.Navigator>
              <Stack.Screen
                name="NavigateCard"
                component={NavigateCard}
                options={{
                  headerShown: false,
                }}
              />
              <Stack.Screen
                name="RideOptionsCard"
                component={RideOptionsCard}
                options={{
                  headerShown: false,
                }}
              />
            </Stack.Navigator>
          </View>
        </View>
      )}

    </View>
  );
};

export default MapScreen;
